import React, { useContext } from "react";
import Form from "react-bootstrap/Form";
import { Context } from "../Context/context";

const Help = () => {
  const ctx = useContext(Context);

  const handlerValueSelect = (e) => {
    ctx.searchUserSelect(e.target.value);
  };
  const checkMinor = (e) => {
    ctx.searchUserCheckMinor(e.target.checked);
    console.log(e.target.checked);
  };

  const checkBetter = (e) => {
    ctx.searchUserCheckBetter(e.target.checked);
    console.log(e.target.checked);
  };

  return (
    <>
      <Form>
        <div className="mt-3 ms-3">
          <Form.Check
            className="fw-bold"
            type="checkbox"
            label="Bigger Amount Donate"
            onChange={checkBetter}
          />
        </div>
      </Form>
      <Form>
        <div className="mt-3 ms-3">
          <Form.Check
            className="fw-bold"
            type="checkbox"
            label="Minor Amount Donate"
            onChange={checkMinor}
          />
        </div>
      </Form>
      <Form>
        <div className="mt-3 ms-3">
          <Form.Check
            className="fw-bold"
            type="checkbox"
            label="default checkbox"
          />
        </div>
      </Form>
      <Form.Check
        type="switch"
        id="custom-switch"
        label="Check this switch"
        className="mt-3 ms-3"
      />
      <Form.Select
        className="ms-1 mt-2"
        aria-label="Default select example"
        onChange={handlerValueSelect}
      >
        <option>Order By Amount Donate</option>
        <option className="fw-bold" value="1">
          Ascendente
        </option>
        <option className="fw-bold" value="2">
          Descendente
        </option>
      </Form.Select>
    </>
  );
};

export default Help;
