import React, { useEffect, useReducer, useState } from "react";
import { getUser } from "../../actions/service";
import Reducer from "./Reducer";

const url = "https://localhost:44376/user/user";

const userDefault = {
  users: [],
};
export const Context = React.createContext(userDefault);

// Provider Component
export const Provider = (props) => {
  const [users, setUser] = useState([]);

  useEffect(() => {
    getUser(url)
      .then((response) => setUser(response.data))
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const [state, dispatchUserAction] = useReducer(Reducer, userDefault);
  // Actions
  const removeUser = (id) => {
    dispatch({
      type: "REMOVE_USER",
      payload: id,
    });
  };

  const addUser = (user) => {
    dispatch({
      type: "ADD_USER",
      payload: user,
    });
  };

  const editUser = (user) => {
    dispatch({
      type: "EDIT_USER",
      payload: user,
    });
  };
  const userContext = {
    users: state.users,
    addItem: addUser,
    removeItem: removeUser,
    updateUser: editUser,
  };

  return (
    <Context.Provider value={userContext}>{props.children}</Context.Provider>
  );
};

export const Consumer = Context.Consumer;
