import React, { useEffect, useReducer, useState } from "react";
import { getUser } from "../../actions/service";

const Context = React.createContext();
const url = "https://localhost:44376/user/user";

const userDefault = {
  users: [],
};

const userReducer = (state, action) => {
  switch (action.type) {
    case "ADD_USER":
      return {
        ...state,
        users: [action.payload, ...state.users],
      };
  }
};

export const Provider = (props) => {
  const [users, setUser] = useState([]);

  useEffect(() => {
    getUser(url)
      .then((response) => setUser(response.data))
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const [userState, dispatchUserAction] = useReducer(userReducer, userDefault);

  return <Context.Provider value={users}>{props.children}</Context.Provider>;
};

export const Consumer = Context.Consumer;
