import React, { useState } from "react";
import { create } from "../../actions/service";
const url = "https://localhost:44376/user/user/add";

export const Reducer = (state, action) => {
  //const [isTrue, setIsTrue] = useState(true);

  switch (action.type) {
    case "REMOVE_USER":
      console.log(state);
      console.log(action);
      return {
        users: state.users.filter((user) => {
          return user.id !== action.payload;
        }),
      };
    case "SEARCH_USER":
      console.log(state);
      console.log(action);
      if (action.payload == 0) {
        return {
          users: state.users.contains(action.payload),
        };
      } else {
        return {
          users: state.users,
        };
      }
    case "ADD_USER":
      console.log(state);
      console.log(action);
      // create(url, action.payload)
      //   .then((response) => {
      //     console.log(response);
      //   })
      //   .catch((error) => {
      //     setIsTrue(false);
      //     console.log(error);
      //   });
      // if (isTrue) {
      //   return {
      //     users: [...state.users, action.payload],
      //   };
      // } else
      //   return {
      //     users: [...state.users],
      //   };
      return {
        users: [...state.users, action.payload],
      };

    case "EDIT_USER":
      console.log(action);
      const updateUser = action.payload;

      const updateUsers = state.users.map((user) => {
        if (user.id === updateUser.id) {
          return {
            id: updateUser.id,
            name: updateUser.name,
            lastName: updateUser.lastName,
            email: updateUser.email,
            age: updateUser.age,
            amountDonate: updateUser.amountDonate + user.amountDonate,
          };
        }
        return user;
      });
      return {
        users: updateUsers,
      };

    default:
      return state;
  }
};
