import React, { Component } from "react";

const Context = React.createContext();

const Provider = () => {
  state = {
    users: [
      {
        id: 1,
        name: "juan",
        lastName: "Martinez",
        email: "juan@gmail.com",
        age: 24,
        amountDonate: 50,
      },
      {
        id: 2,
        name: "yordan",
        lastName: "perez",
        email: "yordan@gmail.com",
        age: 50,
        amountDonate: 75,
      },
    ],
  };

  return (
    <Context.Provider value={state}>{this.props.children}</Context.Provider>
  );
};

export default Consumer = Context.Consumer;
