import React, { useEffect, useState } from "react";
import { getUser } from "../actions/service";
import ListGroup from "react-bootstrap/ListGroup";
import Table from "react-bootstrap/Table";

const Lista = () => {
  const [user, setUser] = useState([]);
  const [issue, setIssue] = useState({});

  useEffect(() => {
    let query = getUser();
    query
      .then((response) => setUser(response.data))
      .catch((error) =>
        setIssue({
          type: 404,
          message: error.message,
        })
      );
  }, []);

  return (
    (
      <div>
        {user.map((obj) => (
          <ListGroup key={obj.id} horizontal="md" className="my-2">
            <ListGroup.Item variant="primary">{obj.name}</ListGroup.Item>
            <ListGroup.Item onClick={() => console.log(obj.id)}>
              {obj.lastName}
            </ListGroup.Item>
            <ListGroup.Item>{obj.email}</ListGroup.Item>
            <ListGroup.Item>{obj.age}</ListGroup.Item>
          </ListGroup>
        ))}
      </div>
    ),
    (
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Username</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td colSpan={2}>Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
    )
  );
};

export default Lista;
