import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import { UpdateUser } from "../../actions/service";

const url = "https://localhost:44376/user/user/update";

function Example(props) {
  const [show, setShow] = useState(false);
  const [count, setCount] = useState();

  const handleClose = (event) => {
    event.preventDefault();
    setShow(false);
    UpdateUser(url, {
      id: props.user.id,
      name: props.user.name,
      lastName: props.user.lastName,
      age: props.user.age,
      email: props.user.email,
      amountDonate: props.user.amountDonate + event.target.value,
    })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const handleShow = () => setShow(true);

  const handlerCount = (event) => {
    setCount(event.target.value);
  };

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        {props.type}
      </Button>

      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>{props.message}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <>
            <Form.Text id="passwordHelpBlock" muted>
              <p className="mt-2 text-success">
                {"Hi " + `${props.user.name}` + ", thank you so much"}
              </p>
            </Form.Text>
            <Form.Control
              type="number"
              id="inputPassword5"
              aria-describedby="passwordHelpBlock"
              placeholder={props.user.amountDonate}
              onChange={handlerCount}
            />
          </>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Example;
