import Table from "react-bootstrap/Table";
import "./ViewList.css";
import React from "react";
import Modal from "../Modal/Modal";
import Button from "react-bootstrap/Button";
import { DeleteUser } from "../../actions/service";
import { Consumer } from "../../Context/UserContext";

const ViewList = (props) => {
  const url = "https://localhost:44376/user/user/delete/";
  const handlerDelete = (id) => {
    let confirm = window.confirm("Do you want to delete this user?");
    if (confirm) {
      DeleteUser(url, id)
        .then((response) => {
          console.log(response);
        })
        .catch((error) => {
          console.log(error);
        });
    } else console.log("rechazado");
  };

  let i = 1;
  return (
    <>
      <div className="container">
        <div className="mt-5">
          <Table striped bordered hover size="sm">
            <thead>
              <tr className="text-center">
                <th>#</th>
                <th>Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Age</th>
                <th>AmountDonate</th>
                <th>Update</th>
                <th>Delete</th>
              </tr>
            </thead>
            {props.items.map((obj) => (
              <tbody key={obj.id}>
                <tr>
                  <td>{i++}</td>
                  <td>{obj.name}</td>
                  <td>{obj.lastName}</td>
                  <td>{obj.email}</td>
                  <td>{obj.age}</td>
                  <td>{obj.amountDonate}</td>
                  <td>
                    <Modal type="Update" message="Amount Donate" user={obj} />
                  </td>
                  <td>
                    <Button
                      variant="danger"
                      onClick={() => handlerDelete(obj.id)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              </tbody>
            ))}
          </Table>
        </div>
      </div>
    </>
  );
};

export default ViewList;
