import React from "react";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const HandlerError = (props) => {
  let variant = "";

  const notify = (responseType, message) => {
    switch (responseType) {
      case 0:
        toast.danger(message, {
          position: toast.POSITION.BOTTOM_RIGHT,
          autoClose: 2000,
        });
        break;
      case 404:
        variant = "Warning";
        break;
      default:
        variant = "Success";
    }
  };
  return (
    <div>
      <button onClick={() => notify(props.error.type, props.error.message)}>
        Notify !
      </button>
      <ToastContainer />
    </div>
  );
};

export default HandlerError;
