import axios from "axios";

const baseUrl = "https://localhost:44376/user/";

export default {
  dCandidate(url = baseUrl + "user/") {
    return {
      fetchAll: () => axios.get(url),
      fetchById: (id) => axios.get(url + "ById/" + id),
      create: (newRecord) => axios.post(url + "add", newRecord),
      update: (recordUpdate) => axios.put(url + "update", recordUpdate),
      delete: (id) => axios.delete(url + "delete/" + id),
    };
  },
};
