import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { UpdateUser } from "../../actions/service";

const url = "https://localhost:44376/user/user/update";

function Example(props) {
  const [show, setShow] = useState(false);
  const [count, setCount] = useState();

  const handleClose = () => {
    setShow(false);
  };

  const saveChanges = () => {
    setShow(false);
    if (count) {
      UpdateUser(url, {
        id: props.user.id,
        name: props.user.name,
        lastName: props.user.lastName,
        age: props.user.age,
        email: props.user.email,
        amountDonate: parseInt(count),
      })
        .then((response) => {
          //toastr
          toast.success(
            "User " + `${props.user.name}` + " updated succesfully",
            {
              position: toast.POSITION.BOTTOM_RIGHT,
              autoClose: 2000,
            }
          );
        })
        .catch((error) => {
          toast.warn(error.message, {
            position: toast.POSITION.BOTTOM_RIGHT,
            autoClose: 2000,
          });
        });
    }
  };
  const handleShow = () => setShow(true);

  const handlerCount = (event) => {
    setCount(event.target.value);
  };

  return (
    <div>
      <Button href="shop/updated" variant="primary" onClick={handleShow}>
        {props.type}
      </Button>

      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>{props.message}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <>
            <Form.Text id="passwordHelpBlock" muted>
              <p className="mt-2 text-success">
                {"Hi " + `${props.user.name}` + ", thank you so much"}
              </p>
            </Form.Text>
            <Form.Control
              type="number"
              id="inputPassword5"
              aria-describedby="passwordHelpBlock"
              placeholder={props.user.amountDonate}
              onChange={handlerCount}
            />
          </>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={saveChanges}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <ToastContainer />
    </div>
  );
}

export default Example;
