import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const HandlerError = (props) => {
  console.log();
  let variant = "";
  switch (props.type) {
    case 0:
      variant = "Danger";
      break;
    case 404:
      variant = "Warning";
      break;
    default:
      variant = "Success";
  }

  return (
    <div>
      {toast[variant.toLowerCase()](`${props.error.message}`, {
        position: toast.POSITION.BOTTOM_RIGHT,
        autoClose: 8000,
      })}
      <ToastContainer />
    </div>
  );
};
