export const Reducer = (state, action) => {
  const valoresIniciales = state.reducerUsers;

  switch (action.type) {
    case "REMOVE_USER":
      console.log(state);
      console.log(action);
      return {
        users: valoresIniciales.filter((user) => {
          return user.id !== action.payload;
        }),
      };
    case "SEARCH_USER_INPUT":
      console.log(state);
      console.log(action);
      return {
        users: state.users.filter((user) => {
          return user.email === action.payload;
        }),
      };

    case "SEARCH_USER_SELECT":
      console.log(state);
      console.log(action);
      if (action.payload == 1) {
        let user = state.users.sort((a, b) => {
          return b.amountDonate - a.amountDonate;
        });
        return {
          users: user,
        };
      }
      if (action.payload == 2) {
        let user = state.users.sort((a, b) => {
          return a.amountDonate - b.amountDonate;
        });
        return {
          users: user,
        };
      } else {
        return {
          users: state.users,
        };
      }

    case "SEARCH_USER_SWITCH":
      console.log(state);
      console.log(action);
      const result1 = state.users.filter(
        (user) =>
          user.amountDonate > action.payload[0] &&
          user.amountDonate < action.payload[1]
      );

      return {
        users: result1,
      };

    case "SEARCH_USER_CHECK_MINOR":
      console.log(state);
      console.log(action);
      if (action.payload === true) {
        let value = Math.min(...state.users.map((user) => user.amountDonate));
        return {
          users: state.users.filter((user) => {
            return user.amountDonate === value;
          }),
        };
      }

    case "SEARCH_USER_CHECK_BETTER":
      console.log(state);
      console.log(action);
      if (action.payload === true) {
        let value = Math.max(...state.users.map((user) => user.amountDonate));
        return {
          users: state.users.filter((user) => {
            return user.amountDonate === value;
          }),
        };
      }

    case "ADD_USER":
      console.log(state);
      console.log(action);

      return {
        users: [...state.users, action.payload],
      };

    case "EDIT_USER":
      console.log(action);
      const updateUser = action.payload;

      const updateUsers = state.users.map((user) => {
        if (user.id === updateUser.id) {
          return {
            id: updateUser.id,
            name: updateUser.name,
            lastName: updateUser.lastName,
            email: updateUser.email,
            age: updateUser.age,
            amountDonate: updateUser.amountDonate + user.amountDonate,
          };
        }
        return user;
      });
      return {
        users: updateUsers,
      };

    default:
      return state;
  }
};
