import Table from "react-bootstrap/Table";
import "./ViewList.css";
import React, { useContext, useState } from "react";
import Modal from "../Modal/Modal";
import Button from "react-bootstrap/Button";
import { Context } from "../Context/context";
import MessageAlert from "../Alert/MessageAlert";

const ViewList = () => {
  const ctx = useContext(Context);
  let state = true;
  if (ctx.users.lenght > 0) {
    state = false;
  }

  const handlerDelete = (id) => {
    let confirm = window.confirm("Do you want to delete this user?");
    if (confirm) {
      ctx.removeUser(id);
    } else console.log("rechazado");
  };

  let i = 1;
  return (
    <>
      {!loading ? (
        <div className="container">
          <div className="mt-5">
            <Table striped bordered hover size="sm">
              <thead>
                <tr className="text-center">
                  <th>#</th>
                  <th>Name</th>
                  <th>Last Name</th>
                  <th>Email</th>
                  <th>Age</th>
                  <th>AmountDonate</th>
                  <th>Update</th>
                  <th>Delete</th>
                </tr>
              </thead>
              {ctx.users.map((obj, key) => (
                <tbody key={key}>
                  <tr>
                    <td>{i++}</td>
                    <td>{obj.name}</td>
                    <td>{obj.lastName}</td>
                    <td>{obj.email}</td>
                    <td>{obj.age}</td>
                    <td>{obj.amountDonate}</td>
                    <td>
                      <Modal type="Update" message="Amount Donate" user={obj} />
                    </td>
                    <td>
                      <Button
                        variant="danger"
                        onClick={() => handlerDelete(obj.id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                </tbody>
              ))}
            </Table>
          </div>
        </div>
      ) : (
        <MessageAlert variant="danger" message="Users no found!!" />
      )}
    </>
  );
};

export default ViewList;

