import React, { createContext, useEffect, useReducer, useState } from "react";
import { getUser } from "../../actions/service";
import Reducer from "./Reducer";

const url = "https://localhost:44376/user/user";
// Initial State

// Create Context
export const Context = createContext();

// Provider Component: make a wrap al app.js
const Provedor = ({ children }) => {
  const [users, setUser] = useState([]);

  useEffect(() => {
    getUser(url)
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  let userDefault = {
    users: [users],
  };

  const [state, dispatch] = useReducer(Reducer, userDefault);
  // Actions
  const removeUser = (id) => {
    dispatch({
      type: "REMOVE_USER",
      payload: id,
    });
  };

  const addUser = (user) => {
    dispatch({
      type: "ADD_USER",
      payload: user,
    });
  };

  const editUser = (user) => {
    dispatch({
      type: "EDIT_USER",
      payload: user,
    });
  };
  // const userContext = {
  //   users: userState.users,
  //   addItem: addUser,
  //   removeItem: removeUser,
  //   updateUser: editUser,
  // };
  console.log(users);
  return (
    <Context.Provider
      value={{
        users: state.users,
        addUser,
        removeUser,
        editUser,
      }}
    >
      {children}
    </Context.Provider>
  );
};
export default Provedor;
