// Create Context
export const Context = createContext(userDefault);

// Provider Component: make a wrap al app.js
const Provedor = ({ children }) => {
  // const [users, setUser] = useState([]);

  // useEffect(() => {
  //   getUser(url)
  //     .then((response) => {
  //       setUser(response.data);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }, []);

  const [state, dispatch] = useReducer(Reducer, userDefault);
  // Actions
  const removeUser = (id) => {
    dispatch({
      type: "REMOVE_USER",
      payload: id,
    });
  };

  const addUser = (user) => {
    dispatch({
      type: "ADD_USER",
      payload: user,
    });
  };

  const editUser = (user) => {
    dispatch({
      type: "EDIT_USER",
      payload: user,
    });
  };

  return (
    <Context.Provider
      value={{
        users: state.users,
        addUser,
        removeUser,
        editUser,
      }}
    >
      {children}
    </Context.Provider>
  );
};
export default Provedor;
