import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const HandlerError = (props) => {
  console.log(props);

  let variant = "";
  switch (props.type) {
    case 0:
      variant = "Danger";
      break;
    case 400:
      variant = "Primary";
      break;
    case 404:
      variant = "Warning";
      break;
    default:
      variant = "Success";
  }

  return variant;
};
